def Galaxy_Validator(Gname_L,D25_Test_B=False):
    Invaild_Galaxy_List=[]
    for Gname in Gname_L:
        print(Gname)
        """
        D25_S_Maj_Deg=D25_Finder.D25_Finder(Gname)
        D25_S_Maj=D25_S_Maj_Deg*60.0 #D25_S_Maj:-float, D25_Semi_Major_Axis, The D25 Semi Major Axis of the current galaxy in arcmin
        if((D25_S_Maj>10.0) or (D25_S_Maj==10.0) and (D25_Test)):
            Invaild_Galaxy_List.append(Gname)
            continue
        """
        Evt2_File_H_L=File_Query_Code_5.File_Query(Gname,"evt2")
        if(isinstance(Evt2_File_H_L,bool)):
            Invaild_Galaxy_List.append(Gname)
    for Invaild_Galaxy in Invaild_Galaxy_List:
        if Invaild_Galaxy in Gname_L:
            Gname_L.remove(Invaild_Galaxy)
    return [Gname_L,Invaild_Galaxy_List]

